import axios from 'axios';

interface CourseResult {
  title: string;
  platform: 'youtube' | 'udemy' | 'coursera';
  link: string;
  thumbnail?: string;
  price?: string;
  rating?: number;
  instructor?: string;
}

export const searchCourses = async (query: string): Promise<CourseResult[]> => {
  try {
    const results: CourseResult[] = [];
    
    // YouTube Search (assuming you already have this implemented)
    // ... existing YouTube search logic ...

    // Udemy Search
    if (process.env.REACT_APP_UDEMY_CLIENT_ID && process.env.REACT_APP_UDEMY_CLIENT_SECRET) {
      const udemyResponse = await axios.get('https://www.udemy.com/api-2.0/courses', {
        headers: {
          Authorization: `Basic ${btoa(`${process.env.REACT_APP_UDEMY_CLIENT_ID}:${process.env.REACT_APP_UDEMY_CLIENT_SECRET}`)}`,
        },
        params: {
          search: query,
          page_size: 5,
          ordering: 'relevance',
        },
      });

      results.push(...udemyResponse.data.results.map((course: any) => ({
        title: course.title,
        platform: 'udemy',
        link: course.url,
        thumbnail: course.image_480x270,
        price: course.price,
        rating: course.rating,
        instructor: course.visible_instructors[0]?.display_name
      })));
    }

    // Coursera Search
    if (process.env.REACT_APP_COURSERA_API_KEY) {
      const courseraResponse = await axios.get('https://api.coursera.org/api/courses.v1', {
        headers: {
          Authorization: `Bearer ${process.env.REACT_APP_COURSERA_API_KEY}`,
        },
        params: {
          q: query,
          limit: 5,
          fields: 'name,photoUrl,partnerIds,instructorIds',
        },
      });

      results.push(...courseraResponse.data.elements.map((course: any) => ({
        title: course.name,
        platform: 'coursera',
        link: `https://www.coursera.org/learn/${course.slug}`,
        thumbnail: course.photoUrl,
        instructor: course.instructorIds?.[0]
      })));
    }

    return results;
  } catch (error) {
    console.error('Error fetching courses:', error);
    return [];
  }
}
