import React, { useState } from 'react';
import { searchCourses } from '../services/courseApi';
import '../styles/CourseRecommendations.css';

interface Course {
  title: string;
  platform: 'youtube' | 'udemy' | 'coursera';
  link: string;
  thumbnail?: string;
  price?: string;
  rating?: number;
  instructor?: string;
}

const CourseRecommendations: React.FC = () => {
  const [query, setQuery] = useState('');
  const [courses, setCourses] = useState<Course[]>([]);
  const [loading, setLoading] = useState(false);

  const handleSearch = async () => {
    if (!query.trim()) return;
    
    setLoading(true);
    try {
      const results = await searchCourses(query);
      setCourses(results);
    } catch (error) {
      console.error('Error searching courses:', error);
    } finally {
      setLoading(false);
    }
  };

  const getPlatformIcon = (platform: string) => {
    switch (platform) {
      case 'youtube':
        return 'üé•';
      case 'udemy':
        return 'üìö';
      case 'coursera':
        return 'üéì';
      default:
        return 'üìå';
    }
  };

  return (
    <div className="course-recommendations">
      <div className="search-container">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="What do you want to learn?"
          className="search-input"
        />
        <button onClick={handleSearch} className="search-button">
          Search
        </button>
      </div>

      {loading && <div className="loading">Searching courses...</div>}

      <div className="courses-grid">
        {courses.map((course, index) => (
          <div key={index} className="course-card">
            {course.thumbnail && (
              <img src={course.thumbnail} alt={course.title} className="course-thumbnail" />
            )}
            <div className="course-info">
              <h3>{course.title}</h3>
              <div className="platform-badge">
                {getPlatformIcon(course.platform)} {course.platform}
              </div>
              {course.instructor && <p>Instructor: {course.instructor}</p>}
              {course.price && <p>Price: {course.price}</p>}
              {course.rating && <p>Rating: {course.rating.toFixed(1)}‚≠ê</p>}
              <a href={course.link} target="_blank" rel="noopener noreferrer" className="view-course-btn">
                View Course
              </a>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CourseRecommendations;
