import React, { useState, useRef, useEffect } from 'react';
import { GoogleGenerativeAI } from '@google/generative-ai';
import '../styles/ChatBot.css';

const GEMINI_API_KEY = process.env.REACT_APP_GEMINI_API_KEY;

const ChatBot: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState<Array<{ text: string; isUser: boolean }>>([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [apiError, setApiError] = useState<string | null>(null);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const [genAI, setGenAI] = useState<GoogleGenerativeAI | null>(null);
  const [model, setModel] = useState<any>(null);

  // Initialize API client
  useEffect(() => {
    try {
      if (!GEMINI_API_KEY) {
        throw new Error('API key is not configured');
      }
      console.log('Initializing API with key length:', GEMINI_API_KEY.length);
      const ai = new GoogleGenerativeAI(GEMINI_API_KEY);
      setGenAI(ai);
      const m = ai.getGenerativeModel({ model: "gemini-pro" });
      setModel(m);
      setApiError(null);
    } catch (error) {
      console.error('Error initializing API:', error);
      setApiError('Failed to initialize API client');
    }
  }, []);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!inputMessage.trim()) return;
    
    if (!genAI || !model) {
      setMessages(prev => [...prev, { 
        text: "Error: API client is not properly initialized. Please check your API key configuration.", 
        isUser: false 
      }]);
      return;
    }

    const userMessage = inputMessage.trim();
    setMessages(prev => [...prev, { text: userMessage, isUser: true }]);
    setInputMessage('');
    setIsLoading(true);
    setApiError(null);

    try {
      // Create a chat history context for the AI
      const chatHistory = messages.map(msg => 
        msg.isUser ? `User: ${msg.text}` : `Assistant: ${msg.text}`
      ).join('\n');

      // Prepare the prompt with context
      const prompt = `You are an AI course assistant helping users find and learn about educational courses. 
      If you encounter any errors, please respond with a friendly error message.
      Previous conversation:\n${chatHistory}\n\nUser: ${userMessage}\n\nAssistant:`;
      
      console.log('Attempting to send message to API...');
      const result = await model.generateContent(prompt);
      console.log('Response received from API');
      
      if (!result || !result.response) {
        throw new Error('No response from API');
      }

      const botMessage = result.response.text();
      if (!botMessage) {
        throw new Error('Empty response from API');
      }

      setMessages(prev => [...prev, { text: botMessage, isUser: false }]);
    } catch (error) {
      console.error('Error in API call:', error);
      const errorMessage = error instanceof Error ? error.message : 'Unknown error occurred';
      setApiError(errorMessage);
      setMessages(prev => [...prev, { 
        text: "I apologize, but I'm having trouble connecting to my AI service right now. Please try again in a moment.", 
        isUser: false 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="chatbot-container">
      {/* Chat Button */}
      <button 
        className="chat-button"
        onClick={() => setIsOpen(!isOpen)}
      >
        {isOpen ? '√ó' : 'üí¨'}
      </button>

      {/* Chat Window */}
      {isOpen && (
        <div className="chat-window">
          <div className="chat-header">
            <h3>AI Course Assistant</h3>
            {apiError && (
              <div className="error-indicator" title={apiError}>
                ‚ö†Ô∏è
              </div>
            )}
          </div>
          
          <div className="messages-container">
            {messages.length === 0 && (
              <div className="welcome-message">
                Hello! I'm your AI course assistant. I can help you find courses, answer questions about different topics, or provide learning recommendations. How can I help you today?
              </div>
            )}
            {messages.map((message, index) => (
              <div
                key={index}
                className={`message ${message.isUser ? 'user-message' : 'bot-message'}`}
              >
                {message.text}
              </div>
            ))}
            {isLoading && (
              <div className="message bot-message">
                <div className="typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          <form onSubmit={handleSendMessage} className="input-container">
            <input
              type="text"
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              placeholder="Ask about courses or topics..."
              disabled={isLoading}
            />
            <button type="submit" disabled={isLoading || !inputMessage.trim()}>
              Send
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default ChatBot;
